import { useState } from 'react'
import './Counter.css'
import {PropTypes} from 'prop-types'

export default function Counter(){
    const [count, setCount] = useState(0);
    function incrementPArentCounter(by){
        setCount(count + by)
    }

    function decrementPArentCounter(by){
        setCount(count - by)
    }
    function somedefaultParentMethod(){
        console.log("parent")
    }
    return (
        <>
        <span className="totalCount">{count}</span>
  <CounterButton incrementmethod = {incrementPArentCounter}/>
  <CounterButton by ={2} incrementmethod = {incrementPArentCounter}/>
  <CounterButton by ={5} incrementmethod = {incrementPArentCounter}/>
        </>
    )
}
function CounterButton({by, incrementmethod}){
//  [0, f], first one is thw value and the other one is to update the state
    // const state = useState(0)
    const [count, setCount] = useState(0);

    console.log(by)

    function incrementCounter(){
        // state[1](state[0] + 1)
        // console.log(state[0])
        // console.log(state[1])

        setCount(count + by)
        // somedefaultParentMethod();
        incrementmethod(by);
        console.log(count)

        console.log('increment counter')
    }

    function decrementCounter(){
        incrementmethod(by);
        setCount(count - by)
        console.log(count)
        // somedefaultParentMethod();
        console.log('increment counter')
    }
    return (
            <div className = "Counter">
                <span className="count">{count}</span>
                <div>
                <button className="counterButton" 
                        onClick={incrementCounter}
                        >+{by}</button>
                 <button className="counterButton" 
                        onClick={decrementCounter}
                        >-{by}</button>
                </div>
            </div>
    )
}

CounterButton.propTypes = {
    by: PropTypes.number
  }

CounterButton.defaultProps = {
    by: 1
  }
